pragma solidity ^0.8.9;

import './IDebtToken.sol';
import './IBase.sol';

interface IStabilityPool is IBase {
  // --- Events ---

  event StabilityPoolInitialized(address stabilityPoolManagerAddress, address depositTokenAddress);

  event StabilityProvided(address user, uint amount);
  event StabilityWithdrawn(address user, uint amount);
  event StabilityGainsWithdrawn(address user, uint depositLost, TokenAmount[] gainsWithdrawn);

  // used as trigger to update the users compounded deposit and current coll gains (there is not user specific event for that)
  event StabilityOffset(uint removedDeposit, TokenAmount[] addedGains);

  event P_Updated(uint _P);
  event S_Updated(address _tokenAddress, uint _S, uint128 _epoch, uint128 _scale);
  event EpochUpdated(uint128 _currentEpoch);
  event ScaleUpdated(uint128 _currentScale);
  event DepositSnapshotUpdated(address indexed _depositor);
  // event G_Updated(uint _G, uint128 _epoch, uint128 _scale);

  // --- Errors  ---

  error NotFromStabilityPoolManager();
  error ZeroAmount();
  error NotOneTrove();

  // --- Functions ---

  function getDepositToken() external view returns (IDebtToken);

  function getTotalDeposit() external view returns (uint);

  function getDepositorDeposit(address _depositor) external view returns (uint);

  function getCompoundedDebtDeposit(address _depositor) external view returns (uint);

  function getTotalGainedColl() external view returns (TokenAmount[] memory coll);

  function getDepositorCollGain(address _depositor, address _collToken) external view returns (uint);

  function getDepositorCollSnapshot(address _depositor, address _collToken) external view returns (uint);

  function provideToSP(address user, uint _amount) external;

  function withdrawFromSP(address user, uint _amount) external;

  function withdrawGains(address user) external;

  function offset(uint _debtToOffset, TokenAmount[] memory _collToAdd) external;
}
","keccak256":"0x06a29f5b91ef199ee97ca6da3d02425eeb24045a78c8167e4d44f10aa6c9181c","license":"MIT"},"contracts/Interfaces/IStabilityPoolManager.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import './IBBase.sol';

interface IStabilityPoolManager is IBBase {
  // --- Events ---

  event StabilityPoolManagerInitiated(
    address liquidationOperationsAddress,
    address storgePoolAddress,
    address reservePoolAddress,
    address debtTokenManagerAddress,
    address priceFeedAddress
  );
  event StabilityPoolAdded(address stabilityPoolAddress);

  // --- Custom Errors ---
  error NotFromLiquidationOps();
  error NotFromReservePool();
  error PoolNotExist();
  error PoolExist();
  error Unauthorized();

  // --- Functions ---
  function getStabilityPool(IDebtToken _debtToken) external view returns (IStabilityPool);

  function getRemainingStability(
    address[] memory collTokenAddresses
  ) external view returns (RemainingStability[] memory);

  function getTotalDeposits() external view returns (TokenAmount[] memory deposits);

  function getTotalDeposit(address _debtTokenAddress) external view returns (uint amount);

  function getDepositorDeposits(address _depositor) external view returns (TokenAmount[] memory deposits);

  function getDepositorDeposit(address _depositor, address _debtTokenAddress) external view returns (uint amount);

  function getCompoundedDeposits() external view returns (TokenAmount[] memory deposits);

  function getDepositorCompoundedDeposit(
    address _depositor,
    address _debtTokenAddress
  ) external view returns (uint amount);

  function getDepositorCompoundedDeposits(address _depositor) external view returns (TokenAmount[] memory deposits);

  function getDepositorCollGains(
    address _depositor,
    address[] memory collTokenAddresses
  ) external view returns (TokenAmount[] memory collGains);

  function provideStability(TokenAmount[] memory _debts) external;

  function withdrawStability(TokenAmount[] memory _debts) external;

  function withdrawGains() external;

  function offset(RemainingStability[] memory _toOffset) external;

  function addStabilityPool(IDebtToken _debtToken) external;
}
