  // --- Liquidation functions ---

  /*
   * Cancels out the specified debt against the tokens contained in the Stability Pool (as far as possible)
   * and transfers the Trove's collateral from ActivePool to StabilityPool.
   * Only called by liquidation functions in the TroveManager.
   */
  function offset(uint _debtToOffset, TokenAmount[] memory _collToAdd) external override {
    _requireCallerIsStabilityPoolManager();

    uint _totalDeposits = totalDeposits;
    if (_totalDeposits == 0) return;

    // adding coll token address into the usedCollTokens array, if they are not already there
    for (uint i = 0; i < _collToAdd.length; i++) {
      TokenAmount memory collEntry = _collToAdd[i];

      bool found = false;
      for (uint ii = 0; ii < usedCollTokens.length; ii++) {
        if (usedCollTokens[ii] != collEntry.tokenAddress) continue;
        found = true;
        break;
      }
      if (!found) usedCollTokens.push(collEntry.tokenAddress);
    }

    (TokenAmount[] memory collGainPerUnitStaked, uint depositLossPerUnitStaked) = _computeRewardsPerUnitStaked(
      _collToAdd,
      _debtToOffset,
      _totalDeposits
    );

    _updateRewardSumAndProduct(collGainPerUnitStaked, depositLossPerUnitStaked); // updates S and P

    totalDeposits -= _debtToOffset;
    emit StabilityOffset(_debtToOffset, _collToAdd);
  }

  function _computeRewardsPerUnitStaked(
    TokenAmount[] memory _collToAdd,
    uint _depositToOffset,
    uint _totalDeposits
  ) internal returns (TokenAmount[] memory collGainPerUnitStaked, uint depositLossPerUnitStaked) {
    /*
     * Compute the rewards. Uses a "feedback" error correction, to keep
     * the cumulative error in the P and S state variables low:
     *
     * 1) Form numerators which compensate for the floor division errors that occurred the last time this
     * function was called.
     * 2) Calculate "per-unit-staked" ratios.
     * 3) Multiply each ratio back by its denominator, to reveal the current floor division error.
     * 4) Store these errors for use in the next correction when this function is called.
     * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
     */

    assert(_depositToOffset <= _totalDeposits);

    collGainPerUnitStaked = new TokenAmount[](_collToAdd.length);
    for (uint i = 0; i < _collToAdd.length; i++) {
      address tokenAddress = _collToAdd[i].tokenAddress;
      collGainPerUnitStaked[i].tokenAddress = tokenAddress;

      uint collNumerator = _collToAdd[i].amount * DECIMAL_PRECISION + lastErrorOffset[tokenAddress];
      collGainPerUnitStaked[i].amount = collNumerator / _totalDeposits;
      lastErrorOffset[tokenAddress] = collNumerator - (collGainPerUnitStaked[i].amount * _totalDeposits);

      totalGainedColl[tokenAddress] += _collToAdd[i].amount;
    }

    if (_depositToOffset == _totalDeposits) {
      depositLossPerUnitStaked = DECIMAL_PRECISION; // When the Pool depletes to 0, so does each deposit
      lastErrorOffset[address(depositToken)] = 0;
    } else {
      uint depositLossNumerator = _depositToOffset * DECIMAL_PRECISION - lastErrorOffset[address(depositToken)];
      /*
       * Add 1 to make error in quotient positive. We want "slightly too much" deposit loss,
       * which ensures the error in any given compoundedLUSDDeposit favors the Stability Pool.
       */
      depositLossPerUnitStaked = depositLossNumerator / _totalDeposits + 1;
      lastErrorOffset[address(depositToken)] = depositLossPerUnitStaked * _totalDeposits - depositLossNumerator;
    }

    return (collGainPerUnitStaked, depositLossPerUnitStaked);
  }

  // Update the Stability Pool reward sum S and product P
  function _updateRewardSumAndProduct(
    TokenAmount[] memory collGainPerUnitStaked,
    uint depositLossPerUnitStaked
  ) internal {
    assert(depositLossPerUnitStaked <= DECIMAL_PRECISION);

    /*
     * The newProductFactor is the factor by which to change all deposits, due to the depletion of Stability Pool deposit in the liquidation.
     * We make the product factor 0 if there was a pool-emptying. Otherwise, it is (1 - depositLossPerUnitStaked)
     */

    uint currentP = P;

    uint newP;
    uint128 currentScaleCached = currentScale;
    uint128 currentEpochCached = currentEpoch;

    /*
     * Calculate the new S first, before we update P.
     * The coll gain for any given depositor from a liquidation depends on the value of their deposit
     * (and the value of totalDeposits) prior to the Stability being depleted by the debt in the liquidation.
     *
     * Since S corresponds to coll gain, and P to deposit loss, we update S first.
     */
    for (uint i = 0; i < collGainPerUnitStaked.length; i++) {
      address tokenAddress = collGainPerUnitStaked[i].tokenAddress;
      uint currentS = epochToScaleToCollTokenToSum[currentEpochCached][currentScaleCached][tokenAddress];
      uint marginalCollGain = collGainPerUnitStaked[i].amount * currentP;
      uint newS = currentS + marginalCollGain;
      epochToScaleToCollTokenToSum[currentEpochCached][currentScaleCached][tokenAddress] = newS;
      emit S_Updated(tokenAddress, newS, currentEpochCached, currentScaleCached);
    }

    // If the Stability Pool was emptied, increment the epoch, and reset the scale and product P
    uint newProductFactor = uint(DECIMAL_PRECISION) - depositLossPerUnitStaked;
    if (newProductFactor == 0) {
      currentEpoch = currentEpochCached + 1;
      emit EpochUpdated(currentEpoch);
      currentScale = 0;
      emit ScaleUpdated(currentScale);
      newP = DECIMAL_PRECISION;

      // If multiplying P by a non-zero product factor would reduce P below the scale boundary, increment the scale
    } else if ((currentP * newProductFactor) / DECIMAL_PRECISION < SCALE_FACTOR) {
      newP = (currentP * newProductFactor * SCALE_FACTOR) / DECIMAL_PRECISION;
      currentScale = currentScaleCached + 1;
      emit ScaleUpdated(currentScale);
    } else {
      newP = (currentP * newProductFactor) / DECIMAL_PRECISION;
    }

    assert(newP > 0);
    P = newP;
    emit P_Updated(newP);
  }

  // --- Reward calculator functions ---

  function _payoutCollGains(address _depositor, uint remainingDeposit) internal {
    TokenAmount[] memory collGains = new TokenAmount[](usedCollTokens.length);
    for (uint i = 0; i < usedCollTokens.length; i++) {
      address collTokenAddress = usedCollTokens[i];
      uint collGain = getDepositorCollGain(_depositor, collTokenAddress);
      if (collGain == 0) continue;

      totalGainedColl[collTokenAddress] -= collGain;
      IERC20(collTokenAddress).transfer(_depositor, collGain);
      collGains[i] = TokenAmount(collTokenAddress, collGain);
    }

    uint initialDeposit = deposits[_depositor];
    uint depositLoss = initialDeposit - remainingDeposit;
    emit StabilityGainsWithdrawn(_depositor, depositLoss, collGains);
  }

  /* Calculates the gains earned by the deposit since its last snapshots were taken.
   * Given by the formula:  E = d0 * (S - S(0))/P(0)
   * where S(0) and P(0) are the depositor's snapshots of the sum S and product P, respectively.
   * d0 is the last recorded deposit value.
   */
  function getDepositorCollGain(address _depositor, address _collToken) public view override returns (uint collGain) {
    uint initialDeposit = deposits[_depositor];
    if (initialDeposit == 0) return 0;

    Snapshots storage snapshots = depositSnapshots[_depositor];
    collGain = _getCollGainFromSnapshots(initialDeposit, _collToken, snapshots);
    return collGain;
  }

  function _getCollGainFromSnapshots(
    uint initialDeposit,
    address collToken,
    Snapshots storage snapshots
  ) internal view returns (uint) {
    /*
     * Grab the sum 'S' from the epoch at which the stake was made. The coll gain may span up to one scale change.
     * If it does, the second portion of the gain is scaled by 1e9.
     * If the gain spans no scale change, the second portion will be 0.
     */
    uint128 epochSnapshot = snapshots.epoch;
    uint128 scaleSnapshot = snapshots.scale;
    uint firstPortion = epochToScaleToCollTokenToSum[epochSnapshot][scaleSnapshot][collToken] -
      snapshots.sums[collToken];
    uint secondPortion = epochToScaleToCollTokenToSum[epochSnapshot][scaleSnapshot + 1][collToken] / SCALE_FACTOR;

    uint collGain = (initialDeposit * (firstPortion + secondPortion)) / snapshots.P / DECIMAL_PRECISION;
    return collGain;
  }

  // --- Compounded deposit ---

  function getDepositorDeposit(address _depositor) external view override returns (uint) {
    return deposits[_depositor];
  }

  /*
   * Return the user's compounded deposit. Given by the formula:  d = d0 * P/P(0)
   * where P(0) is the depositor's snapshot of the product P, taken when they last updated their deposit.
   */
  function getCompoundedDebtDeposit(address _depositor) public view override returns (uint) {
    uint initialDeposit = deposits[_depositor];
    if (initialDeposit == 0) return 0;

    Snapshots storage snapshots = depositSnapshots[_depositor];
    uint compoundedDeposit = _getCompoundedStakeFromSnapshots(initialDeposit, snapshots);
    return compoundedDeposit;
  }

  // Internal function, used to calculcate compounded deposits.
  function _getCompoundedStakeFromSnapshots(
    uint _initialStake,
    Snapshots storage _snapshots
  ) internal view returns (uint) {
    // If stake was made before a pool-emptying event, then it has been fully cancelled with debt -- so, return 0
    if (_snapshots.epoch < currentEpoch) return 0;

    uint compoundedStake;
    uint128 scaleDiff = currentScale - _snapshots.scale;

    /* Compute the compounded stake. If a scale change in P was made during the stake's lifetime,
     * account for it. If more than one scale change was made, then the stake has decreased by a factor of
     * at least 1e-9 -- so return 0.
     */
    if (scaleDiff == 0) compoundedStake = (_initialStake * P) / _snapshots.P;
    else if (scaleDiff == 1) compoundedStake = (_initialStake * P) / _snapshots.P / SCALE_FACTOR;
    else compoundedStake = 0; // if scaleDiff >= 2

    /*
     * If compounded deposit is less than a billionth of the initial deposit, return 0.
     *
     * NOTE: originally, this line was in place to stop rounding errors making the deposit too large. However, the error
     * corrections should ensure the error in P "favors the Pool", i.e. any given compounded deposit should slightly less
     * than it's theoretical value.
     *
     * Thus it's unclear whether this line is still really needed.
     */
    if (compoundedStake < _initialStake / 1e9) return 0;

    return compoundedStake;
  }

  // --- Stability Pool Deposit Functionality ---

  function _updateDepositAndSnapshots(address _depositor, uint _newValue) internal {
    deposits[_depositor] = _newValue;

    if (_newValue == 0) {
      delete depositSnapshots[_depositor];
      emit DepositSnapshotUpdated(_depositor);
      return;
    }

    uint128 currentScaleCached = currentScale;
    depositSnapshots[_depositor].scale = currentScaleCached;

    uint128 currentEpochCached = currentEpoch;
    depositSnapshots[_depositor].epoch = currentEpochCached;

    uint currentP = P;
    depositSnapshots[_depositor].P = currentP;

    for (uint i = 0; i < usedCollTokens.length; i++) {
      depositSnapshots[_depositor].sums[usedCollTokens[i]] = epochToScaleToCollTokenToSum[currentEpochCached][
        currentScaleCached
      ][usedCollTokens[i]];
    }

    emit DepositSnapshotUpdated(_depositor);
  }

  // --- 'require' functions ---

  function _requireCallerIsStabilityPoolManager() internal view {
    if (msg.sender != stabilityPoolManagerAddress) revert NotFromStabilityPoolManager();
  }

  function _requireUserHasDeposit(uint _initialDeposit) internal pure {
    if (_initialDeposit == 0) revert ZeroAmount();
  }

  function _requireNonZeroAmount(uint _amount) internal pure {
    if (_amount == 0) revert ZeroAmount();
  }
}
","keccak256":"0xe658c79dceb541248f0b1139af4f8557961fcaf698466b3c8cb97e9aa0175c22","license":"MIT"},"contracts/StabilityPoolManager.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import '@openzeppelin/contracts/access/Ownable.sol';
import './Interfaces/IDebtToken.sol';
import './Dependencies/CheckContract.sol';
import './Interfaces/IDebtTokenManager.sol';
import './Dependencies/LiquityBase.sol';
import './Interfaces/IStabilityPool.sol';
import './Interfaces/IStabilityPoolManager.sol';
import './Interfaces/ITroveManager.sol';
import './Interfaces/IStoragePool.sol';
import './Interfaces/IReservePool.sol';
import './Interfaces/IPriceFeed.sol';
import './StabilityPool.sol';

contract StabilityPoolManager is Ownable(msg.sender), CheckContract, IStabilityPoolManager {
  string public constant NAME = 'StabilityPoolManager';

  address public liquidationOperationsAddress;
  IStoragePool public storagePool;
  IReservePool public reservePool;
  IPriceFeed public priceFeed;
  address public debtTokenManagerAddress;

  // --- Data structures ---

  mapping(IDebtToken => IStabilityPool) public stabilityPools;
  IStabilityPool[] public stabilityPoolsArray;

  // --- Dependency setter ---

  function setAddresses(
    address _liquidationOperationsAddress,
    address _priceFeedAddress,
    address _storagePoolAddress,
    address _reservePoolAddress,
    address _debtTokenManagerAddress
  ) external onlyOwner {
    checkContract(_liquidationOperationsAddress);
    checkContract(_priceFeedAddress);
    checkContract(_storagePoolAddress);
    checkContract(_reservePoolAddress);
    checkContract(_debtTokenManagerAddress);

    liquidationOperationsAddress = _liquidationOperationsAddress;
    priceFeed = IPriceFeed(_priceFeedAddress);
    storagePool = IStoragePool(_storagePoolAddress);
    reservePool = IReservePool(_reservePoolAddress);
    debtTokenManagerAddress = _debtTokenManagerAddress;

    emit StabilityPoolManagerInitiated(
      _liquidationOperationsAddress,
      _storagePoolAddress,
      _reservePoolAddress,
      _debtTokenManagerAddress,
      _priceFeedAddress
    );

    renounceOwnership();
  }

  // --- Getters ---

  function getStabilityPool(IDebtToken _debtToken) external view override returns (IStabilityPool) {
    IStabilityPool stabilityPool = stabilityPools[_debtToken];
    if (address(stabilityPool) == address(0)) revert PoolNotExist();
    return stabilityPool;
  }

  function getStabilityPools() external view returns (IStabilityPool[] memory) {
    return stabilityPoolsArray;
  }

  function getRemainingStability(
    address[] memory collTokenAddresses
  ) external view override returns (RemainingStability[] memory) {
    RemainingStability[] memory remainingStability = new RemainingStability[](stabilityPoolsArray.length);

    for (uint i = 0; i < stabilityPoolsArray.length; i++) {
      TokenAmount[] memory collGained = new TokenAmount[](collTokenAddresses.length);
      for (uint a = 0; a < collTokenAddresses.length; a++) collGained[a] = TokenAmount(collTokenAddresses[a], 0);

      remainingStability[i] = RemainingStability({
        stabilityPool: stabilityPoolsArray[i],
        tokenAddress: address(stabilityPoolsArray[i].getDepositToken()),
        remaining: stabilityPoolsArray[i].getTotalDeposit(),
        debtToOffset: 0,
        collGained: collGained
      });
    }

    return remainingStability;
  }

  function getTotalDeposits() external view override returns (TokenAmount[] memory deposits) {
    deposits = new TokenAmount[](stabilityPoolsArray.length);
    for (uint i = 0; i < stabilityPoolsArray.length; i++) {
      deposits[i] = TokenAmount(
        address(stabilityPoolsArray[i].getDepositToken()),
        stabilityPoolsArray[i].getTotalDeposit()
      );
    }

    return deposits;
  }

  function getTotalDeposit(address _debtTokenAddress) external view returns (uint amount) {
    IStabilityPool pool = stabilityPools[IDebtToken(_debtTokenAddress)];
    if (address(pool) == address(0)) return 0;
    return pool.getTotalDeposit();
  }

  function getCompoundedDeposits() external view override returns (TokenAmount[] memory deposits) {
    deposits = new TokenAmount[](stabilityPoolsArray.length);
    for (uint i = 0; i < stabilityPoolsArray.length; i++) {
      deposits[i] = TokenAmount(
        address(stabilityPoolsArray[i].getDepositToken()),
        stabilityPoolsArray[i].getCompoundedDebtDeposit(msg.sender)
      );
    }

    return deposits;
  }

  function getDepositorDeposits(address _depositor) external view override returns (TokenAmount[] memory deposits) {
    deposits = new TokenAmount[](stabilityPoolsArray.length);
    for (uint i = 0; i < stabilityPoolsArray.length; i++) {
      deposits[i] = TokenAmount(
        address(stabilityPoolsArray[i].getDepositToken()),
        stabilityPoolsArray[i].getDepositorDeposit(_depositor)
      );
    }

    return deposits;
  }

  function getDepositorDeposit(
    address _depositor,
    address _debtTokenAddress
  ) external view override returns (uint amount) {
    IStabilityPool pool = stabilityPools[IDebtToken(_debtTokenAddress)];
    if (address(pool) == address(0)) return 0;
    return pool.getDepositorDeposit(_depositor);
  }

  function getDepositorCompoundedDeposit(
    address _depositor,
    address _debtTokenAddress
  ) external view override returns (uint amount) {
    IStabilityPool pool = stabilityPools[IDebtToken(_debtTokenAddress)];
    if (address(pool) == address(0)) return 0;
    return pool.getCompoundedDebtDeposit(_depositor);
  }

  function getDepositorCompoundedDeposits(
    address _depositor
  ) external view override returns (TokenAmount[] memory deposits) {
    deposits = new TokenAmount[](stabilityPoolsArray.length);
    for (uint i = 0; i < stabilityPoolsArray.length; i++) {
      deposits[i] = TokenAmount(
        address(stabilityPoolsArray[i].getDepositToken()),
        stabilityPoolsArray[i].getCompoundedDebtDeposit(_depositor)
      );
    }

    return deposits;
  }

  function getDepositorCollGains(
    address _depositor,
    address[] memory collTokenAddresses
  ) external view override returns (TokenAmount[] memory collGains) {
    collGains = new TokenAmount[](collTokenAddresses.length);
    for (uint i = 0; i < collTokenAddresses.length; i++) {
      for (uint ii = 0; ii < stabilityPoolsArray.length; ii++) {
        collGains[i] = TokenAmount(
          address(stabilityPoolsArray[ii].getDepositToken()),
          stabilityPoolsArray[ii].getDepositorCollGain(_depositor, collTokenAddresses[i])
        );
      }
    }

    return collGains;
  }

  // --- Setters ---

  function provideStability(TokenAmount[] memory _debts) external override {
    address user = msg.sender;

    for (uint i = 0; i < _debts.length; i++) {
      IDebtToken debtToken = IDebtToken(_debts[i].tokenAddress);
      IStabilityPool stabilityPool = stabilityPools[debtToken];
      if (address(stabilityPool) == address(0)) revert PoolNotExist();

      debtToken.sendToPool(user, address(stabilityPool), _debts[i].amount);
      stabilityPool.provideToSP(user, _debts[i].amount);
    }
  }

  function withdrawStability(TokenAmount[] memory _debts) external override {
    address user = msg.sender;

    for (uint i = 0; i < _debts.length; i++) {
      IDebtToken debtToken = IDebtToken(_debts[i].tokenAddress);
      IStabilityPool stabilityPool = stabilityPools[debtToken];
      if (address(stabilityPool) == address(0)) revert PoolNotExist();

      stabilityPool.withdrawFromSP(user, _debts[i].amount);
    }
  }

  function withdrawGains() external override {
    address user = msg.sender;

    for (uint i = 0; i < stabilityPoolsArray.length; i++) {
      IStabilityPool stabilityPool = stabilityPoolsArray[i];
      stabilityPool.withdrawGains(user);
    }
  }

  function offset(RemainingStability[] memory _toOffset) external override {
    _requireCallerIsLiquidationOps();

    IStoragePool storagePoolCached = storagePool;
    for (uint i = 0; i < _toOffset.length; i++) {
      RemainingStability memory remainingStability = _toOffset[i];
      address stabilityPoolAddress = address(remainingStability.stabilityPool);
      if (remainingStability.debtToOffset == 0) continue;

      // Burn the debt that was successfully offset
      remainingStability.stabilityPool.getDepositToken().burn(stabilityPoolAddress, remainingStability.debtToOffset);

      // move the coll from the active pool into the stability pool
      IDebtToken stableDebt = reservePool.stableDebtToken();
      IERC20 govToken = reservePool.govToken();
      uint stableCollIndex = remainingStability.collGained.length; // out range index as default
      uint govTokenCollIndex = remainingStability.collGained.length;

      for (uint ii = 0; ii < remainingStability.collGained.length; ii++) {
        if (remainingStability.collGained[ii].tokenAddress == address(stableDebt)) stableCollIndex = ii;
        if (remainingStability.collGained[ii].tokenAddress == address(govToken)) govTokenCollIndex = ii;
        if (remainingStability.collGained[ii].amount == 0) continue;

        storagePoolCached.withdrawalValue(
          stabilityPoolAddress,
          remainingStability.collGained[ii].tokenAddress,
          true,
          PoolType.Active,
          remainingStability.collGained[ii].amount
        );
      }

      // check possible loss
      uint gainedCollValue = _getGainedCollValue(remainingStability.collGained);
      uint offsetValue = priceFeed.getUSDValue(remainingStability.tokenAddress, remainingStability.debtToOffset);
      if (offsetValue > gainedCollValue) {
        // Repay loss from reserve pool
        (uint repaidGov, uint repaidStable) = reservePool.withdrawValue(
          stabilityPoolAddress,
          offsetValue - gainedCollValue
        );

        // add repaid gov token to coll gained array
        if (repaidGov > 0) {
          if (govTokenCollIndex >= remainingStability.collGained.length) {
            // govTokenCollIndex not found in prev loop, add to end of array
            TokenAmount[] memory collGained = new TokenAmount[](remainingStability.collGained.length + 1);
            for (uint ii = 0; ii < remainingStability.collGained.length; ii++) {
              collGained[ii] = remainingStability.collGained[ii];
            }
            collGained[remainingStability.collGained.length] = TokenAmount({
              tokenAddress: address(govToken),
              amount: repaidGov
            });
            remainingStability.collGained = collGained;
          } else {
            remainingStability.collGained[govTokenCollIndex].amount += repaidGov;
          }
        }
        // add repaid stableCoin to coll gained array
        if (repaidStable > 0) {
          if (stableCollIndex >= remainingStability.collGained.length) {
            // stablecoinIndex not found in prev loop, add to end of array
            TokenAmount[] memory collGained = new TokenAmount[](remainingStability.collGained.length + 1);
            for (uint ii = 1; ii < remainingStability.collGained.length; ii++) {
              collGained[ii] = remainingStability.collGained[ii];
            }
            collGained[remainingStability.collGained.length] = TokenAmount({
              tokenAddress: address(stableDebt),
              amount: repaidStable
            });
            remainingStability.collGained = collGained;
          } else {
            remainingStability.collGained[stableCollIndex].amount += repaidStable;
          }
        }
      }

      // update internal pool stake snapshots
      remainingStability.stabilityPool.offset(remainingStability.debtToOffset, remainingStability.collGained);

      // Cancel the liquidated debt with the debt in the stability pool
      storagePoolCached.subtractValue(
        remainingStability.tokenAddress,
        false,
        PoolType.Active,
        remainingStability.debtToOffset
      );
    }
  }

  function addStabilityPool(IDebtToken _debtToken) external override {
    if (msg.sender != debtTokenManagerAddress) revert Unauthorized();
    if (address(stabilityPools[_debtToken]) != address(0)) revert PoolExist();

    IStabilityPool stabilityPool = new StabilityPool(address(this), address(_debtToken));

    stabilityPools[_debtToken] = stabilityPool;
    stabilityPoolsArray.push(stabilityPool);
    emit StabilityPoolAdded(address(stabilityPool));
  }

  function _getGainedCollValue(TokenAmount[] memory collGained) internal view returns (uint gainedValue) {
    for (uint i = 0; i < collGained.length; i++) {
      gainedValue += priceFeed.getUSDValue(collGained[i].tokenAddress, collGained[i].amount);
    }
  }

  function _requireCallerIsLiquidationOps() internal view {
    if (msg.sender != liquidationOperationsAddress) revert NotFromLiquidationOps();
  }

  function _requireCallerIsReservePool() internal view {
    if (msg.sender != address(reservePool)) revert NotFromReservePool();
  }
}
