pragma solidity ^0.8.9;

import './Interfaces/ISwapERC20.sol';

contract SwapERC20 is ISwapERC20 {
  string public constant name = 'Swap';
  string public constant symbol = 'SWAP';
  uint8 public constant decimals = 18;
  uint public totalSupply;
  mapping(address => uint) public balanceOf;
  mapping(address => mapping(address => uint)) public allowance;

  bytes32 public DOMAIN_SEPARATOR;
  // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
  bytes32 public constant PERMIT_TYPEHASH = 0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
  mapping(address => uint) public nonces;

  constructor() {
    uint chainId;
    assembly {
      chainId := chainid()
    }

    DOMAIN_SEPARATOR = keccak256(
      abi.encode(
        keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)'),
        keccak256(bytes(name)),
        keccak256(bytes('1')),
        chainId,
        address(this)
      )
    );
  }

  function _mint(address to, uint value) internal {
    totalSupply += value;
    balanceOf[to] += value;
    emit Transfer(address(0), to, value);
  }

  function _burn(address from, uint value) internal {
    balanceOf[from] -= value;
    totalSupply -= value;
    emit Transfer(from, address(0), value);
  }
}
","keccak256":"0xd13454b818b8b5f5b550f5c71be45e8396d0cf0bd4582a069b3f2a03ea00d077","license":"MIT"},"contracts/SwapOperations.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import './SwapPair.sol';
import './Dependencies/LiquityBase.sol';
import './Interfaces/ISwapOperations.sol';
import './Interfaces/IBorrowerOperations.sol';
import './Interfaces/IDebtTokenManager.sol';
import './Dependencies/CheckContract.sol';
import './Interfaces/ITroveManager.sol';

contract SwapOperations is ISwapOperations, Ownable(msg.sender), CheckContract, LiquityBase {

  // --- Connected contract declarations ---

  ITroveManager public troveManager;
  IBorrowerOperations public borrowerOperations;
  address public priceFeedAddress;
  IDebtTokenManager public debtTokenManager;

  // --- Data structures ---

  mapping(address => mapping(address => address)) public getPair;
  address[] public allPairs;

  // --- Dependency setters ---

  function setAddresses(
    address _borrowerOperationsAddress,
    address _troveManagerAddress,
    address _priceFeedAddress,
    address _debtTokenManager
  ) external onlyOwner {
    checkContract(_borrowerOperationsAddress);
    checkContract(_troveManagerAddress);
    checkContract(_priceFeedAddress);
    checkContract(_debtTokenManager);

    borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);
    troveManager = ITroveManager(_troveManagerAddress);
    priceFeedAddress = _priceFeedAddress;
    debtTokenManager = IDebtTokenManager(_debtTokenManager);

    emit SwapOperationsInitialized(
      _borrowerOperationsAddress,
      _troveManagerAddress,
      _priceFeedAddress,
      _debtTokenManager
    );

    // Disabled to create pairs later
    // renounceOwnership();
  }

  modifier ensure(uint deadline) {
    if (deadline < block.timestamp) revert Expired();
    _;
  }

  // --- Pair Management ---

  function allPairsLength() external view returns (uint) {
    return allPairs.length;
  }

  function createPair(address tokenA, address tokenB) external onlyOwner returns (address pair) {
    if (tokenA == tokenB) revert IdenticalAddresses();
    if (tokenA != address(debtTokenManager.getStableCoin()) && tokenB != address(debtTokenManager.getStableCoin()))
      revert PairRequiresStable();

    (address token0, address token1) = sortTokens(tokenA, tokenB);
    if (token0 == address(0)) revert ZeroAddress();
    if (getPair[token0][token1] != address(0)) revert PairExists(); // single check is sufficient

    bytes memory bytecode = type(SwapPair).creationCode;
    bytes32 salt = keccak256(abi.encodePacked(token0, token1));
    assembly {
      pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
    }

    ISwapPair(pair).initialize(token0, token1, address(debtTokenManager), priceFeedAddress);
    getPair[token0][token1] = pair;
    getPair[token1][token0] = pair; // populate mapping in the reverse direction
    allPairs.push(pair);

    emit PairCreated(token0, token1, pair, allPairs.length);
  }

  // --- Getter functions ---

  function quote(uint amountA, uint reserveA, uint reserveB) public pure virtual override returns (uint amountB) {
    if (amountA == 0) revert InsufficientAmount();
    if (reserveA == 0 || reserveB == 0) revert InsufficientLiquidity();

    amountB = (amountA * reserveB) / reserveA;
  }

  function getAmountOut(
    uint amountIn,
    uint reserveIn,
    uint reserveOut,
    uint32 swapFee
  ) public pure virtual override returns (uint amountOut) {
    if (amountIn == 0) revert InsufficientInputAmount();
    if (reserveIn == 0 || reserveOut == 0) revert InsufficientLiquidity();

    uint amountInWithFee = amountIn * (SWAP_FEE_PRECISION - swapFee);
    uint numerator = amountInWithFee * reserveOut;
    uint denominator = reserveIn * SWAP_FEE_PRECISION + amountInWithFee;
    amountOut = numerator / denominator;
  }

  function getAmountIn(
    uint amountOut,
    uint reserveIn,
    uint reserveOut,
    uint32 swapFee
  ) public pure virtual override returns (uint amountIn) {
    if (amountOut == 0) revert InsufficientOutputAmount();
    if (reserveIn == 0 || reserveOut == 0) revert InsufficientLiquidity();

    uint numerator = reserveIn * amountOut * SWAP_FEE_PRECISION;
    uint denominator = (reserveOut - amountOut) * (SWAP_FEE_PRECISION - swapFee);
    amountIn = (numerator / denominator) + 1;
  }

  function getAmountsOut(
    uint amountIn,
    address[] memory path
  ) public view virtual override returns (uint[] memory amounts) {
    if (path.length < 2) revert InvalidPath();

    amounts = new uint[](path.length);
    amounts[0] = amountIn;
    for (uint i; i < path.length - 1; i++) {
      (uint reserveIn, uint reserveOut, uint32 swapFee) = getReserves(path[i], path[i + 1]);
      amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut, swapFee);
    }
  }

  function getAmountsIn(
    uint amountOut,
    address[] memory path
  ) public view virtual override returns (uint[] memory amounts) {
    if (path.length < 2) revert InvalidPath();

    amounts = new uint[](path.length);
    amounts[amounts.length - 1] = amountOut;
    for (uint i = path.length - 1; i > 0; i--) {
      (uint reserveIn, uint reserveOut, uint32 swapFee) = getReserves(path[i - 1], path[i]);
      amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut, swapFee);
    }
  }

  // --- Liquidity functions ---

  struct ProvidingVars {
    address pair;
    uint senderBalanceA;
    uint senderBalanceB;
    uint fromBalanceA;
    uint fromBalanceB;
    uint fromMintA;
    uint fromMintB;
  }

  function addLiquidity(
    address tokenA,
    address tokenB,
    uint amountADesired,
    uint amountBDesired,
    uint amountAMin,
    uint amountBMin,
    MintMeta memory _mintMeta,
    uint deadline
  ) public virtual override ensure(deadline) returns (uint amountA, uint amountB, uint liquidity) {
    ProvidingVars memory vars;
    vars.pair = getPair[tokenA][tokenB];
    if (vars.pair == address(0)) revert PairDoesNotExist();

    {
      (uint reserveA, uint reserveB, ) = getReserves(tokenA, tokenB);
      if (reserveA == 0 && reserveB == 0) {
        (amountA, amountB) = (amountADesired, amountBDesired);
      } else {
        uint amountBOptimal = quote(amountADesired, reserveA, reserveB);
        if (amountBOptimal <= amountBDesired) {
          if (amountBOptimal < amountBMin) revert InsufficientBAmount();
          (amountA, amountB) = (amountADesired, amountBOptimal);
        } else {
          uint amountAOptimal = quote(amountBDesired, reserveB, reserveA);
          assert(amountAOptimal <= amountADesired);
          if (amountAOptimal < amountAMin) revert InsufficientAAmount();
          (amountA, amountB) = (amountAOptimal, amountBDesired);
        }
      }
    }

    vars.senderBalanceA = IERC20(tokenA).balanceOf(msg.sender);
    vars.senderBalanceB = IERC20(tokenB).balanceOf(msg.sender);

    vars.fromBalanceA = LiquityMath._min(vars.senderBalanceA, amountA);
    vars.fromBalanceB = LiquityMath._min(vars.senderBalanceB, amountB);

    vars.fromMintA = amountA - vars.fromBalanceA;
    vars.fromMintB = amountB - vars.fromBalanceB;

    // mint new tokens if the sender did not have enough
    if (vars.fromMintA != 0 || vars.fromMintB != 0) {
      TokenAmount[] memory debtsToMint;
      if (vars.fromMintA != 0 && vars.fromMintB != 0) {
        // mint both
        debtsToMint = new TokenAmount[](2);
        debtsToMint[0] = TokenAmount(tokenA, vars.fromMintA);
        debtsToMint[1] = TokenAmount(tokenB, vars.fromMintB);
      } else {   
        // mint only 1 token    
        debtsToMint = new TokenAmount[](1);
        debtsToMint[0] = (vars.fromMintA != 0
          ? TokenAmount(tokenA, vars.fromMintA) // mint A
          : TokenAmount(tokenB, vars.fromMintB) // mint B
        );
      }
      borrowerOperations.increaseDebt(msg.sender, vars.pair, debtsToMint, _mintMeta);
    }

    // transfer tokens sourced from senders balance
    if (vars.fromBalanceA != 0) safeTransferFrom(tokenA, msg.sender, vars.pair, vars.fromBalanceA);
    if (vars.fromBalanceB != 0) safeTransferFrom(tokenB, msg.sender, vars.pair, vars.fromBalanceB);

    liquidity = ISwapPair(vars.pair).mint(msg.sender);
  }

  function addLiquidityWithPermit(
    address tokenA,
    address tokenB,
    uint amountADesired,
    uint amountBDesired,
    uint amountAMin,
    uint amountBMin,
    MintMeta memory _mintMeta,
    uint deadline,
    uint8[] memory v,
    bytes32[] memory r,
    bytes32[] memory s
  ) external returns (uint amountA, uint amountB, uint liquidity) {
    IERC20Permit(tokenA).permit(msg.sender, address(this), amountADesired, deadline, v[0], r[0], s[0]);
    IERC20Permit(tokenB).permit(msg.sender, address(this), amountBDesired, deadline, v[1], r[1], s[1]);
    return addLiquidity(tokenA, tokenB, amountADesired, amountBDesired, amountAMin, amountBMin, _mintMeta, deadline);
  }

  struct RemovalVars {
    address token0;
    address token1;
    uint amount0;
    uint amount1;
    uint burned0;
    uint burned1;
  }

  function removeLiquidity(
    address tokenA,
    address tokenB,
    uint liquidity,
    uint amountAMin,
    uint amountBMin,
    address _upperHint,
    address _lowerHint,
    uint deadline
  ) public virtual override ensure(deadline) returns (uint amountA, uint amountB) {
    RemovalVars memory vars;
    (vars.token0, vars.token1) = sortTokens(tokenA, tokenB);

    // receive tokens from pair
    address pair = getPair[tokenA][tokenB];
    (vars.amount0, vars.amount1, vars.burned0, vars.burned1) = ISwapPair(pair).burn(
      msg.sender,
      liquidity,
      // check if there are some debts which has to be repaid first
      troveManager.getTroveRepayableDebt(msg.sender, vars.token0, false),
      troveManager.getTroveRepayableDebt(msg.sender, vars.token1, false)
    );

    // handle trove debt repayment
    if (vars.burned0 != 0 || vars.burned1 != 0) {
      TokenAmount[] memory debtsToRepay;
      if (vars.burned0 != 0 && vars.burned1 != 0) {
        // repay both
        debtsToRepay = new TokenAmount[](2);
        debtsToRepay[0] = TokenAmount(vars.token0, vars.burned0);
        debtsToRepay[1] = TokenAmount(vars.token1, vars.burned1);
      } else {
        // repay only 1 token
        debtsToRepay = new TokenAmount[](1);
        debtsToRepay[0] = (vars.burned0 != 0
          ? TokenAmount(vars.token0, vars.burned0)
          : TokenAmount(vars.token1, vars.burned1)
        );
      }
      borrowerOperations.repayDebtFromPoolBurn(msg.sender, debtsToRepay, _upperHint, _lowerHint);
    }

    (amountA, amountB) = tokenA == vars.token0 ? (vars.amount0, vars.amount1) : (vars.amount1, vars.amount0);
    if (amountA < amountAMin) revert InsufficientAAmount();
    if (amountB < amountBMin) revert InsufficientBAmount();
  }

  // **** SWAP ****
  // requires the initial amount to have already been sent to the first pair
  // --- Swap functions ---

  // requires the initial amount to have already been sent to the first pair
  function _swap(uint[] memory amounts, address[] memory path, address _to) internal virtual {
    for (uint i; i < path.length - 1; i++) {
      (address input, address output) = (path[i], path[i + 1]);
      (address token0, ) = sortTokens(input, output);

      uint amountOut = amounts[i + 1];
      (uint amount0Out, uint amount1Out) = input == token0 ? (uint(0), amountOut) : (amountOut, uint(0));

      address to = i < path.length - 2 ? getPair[output][path[i + 2]] : _to;
      ISwapPair(getPair[input][output]).swap(amount0Out, amount1Out, to);
    }
  }

  function swapExactTokensForTokens(
    uint amountIn,
    uint amountOutMin,
    address[] calldata path,
    address to,
    uint deadline
  ) public virtual override ensure(deadline) returns (uint[] memory amounts) {
    amounts = getAmountsOut(amountIn, path);
    if (amounts[amounts.length - 1] < amountOutMin) revert InsufficientOutputAmount();
    safeTransferFrom(path[0], msg.sender, getPair[path[0]][path[1]], amounts[0]);
    _swap(amounts, path, to);
  }

  function swapTokensForExactTokens(
    uint amountOut,
    uint amountInMax,
    address[] calldata path,
    address to,
    uint deadline
  ) public virtual override ensure(deadline) returns (uint[] memory amounts) {
    amounts = getAmountsIn(amountOut, path);
    if (amounts[0] > amountInMax) revert ExcessiveInputAmount();
    safeTransferFrom(path[0], msg.sender, getPair[path[0]][path[1]], amounts[0]);
    _swap(amounts, path, to);
  }

  function swapExactTokensForTokensWithPermit(
    uint amountIn,
    uint amountOutMin,
    address[] calldata path,
    address to,
    uint deadline,
    uint8 v,
    bytes32 r,
    bytes32 s
  ) external returns (uint[] memory amounts) {
    IERC20Permit(path[0]).permit(msg.sender, address(this), amountIn, deadline, v, r, s);
    return swapExactTokensForTokens(amountIn, amountOutMin, path, to, deadline);
  }
  // --- Position functions ---

  function openLongPosition(
    uint stableToMintIn,
    uint debtOutMin,
    address debtTokenAddress,
    address to,
    MintMeta memory _mintMeta,
    uint deadline
  ) external override ensure(deadline) returns (uint[] memory amounts) {
    address[] memory path = new address[](2);
    path[0] = address(debtTokenManager.getStableCoin());
    path[1] = debtTokenAddress;

    return _openPosition(stableToMintIn, debtOutMin, path, to, _mintMeta);
  }

  function openShortPosition(
    uint debtToMintIn,
    uint stableOutMin,
    address debtTokenAddress,
    address to,
    MintMeta memory _mintMeta,
    uint deadline
  ) external override ensure(deadline) returns (uint[] memory amounts) {
    address[] memory path = new address[](2);
    path[0] = debtTokenAddress;
    path[1] = address(debtTokenManager.getStableCoin());

    return _openPosition(debtToMintIn, stableOutMin, path, to, _mintMeta);
  }

  function _openPosition(
    uint amountIn,
    uint amountOutMin,
    address[] memory path,
    address to,
    MintMeta memory _mintMeta
  ) internal returns (uint[] memory amounts) {
    address pair = getPair[path[0]][path[1]];
    if (pair == address(0)) revert PairDoesNotExist();        

    amounts = getAmountsOut(amountIn, path);
    if (amounts[amounts.length - 1] < amountOutMin) revert InsufficientOutputAmount();

    debtTokenManager.getDebtToken(path[0]); //check if debt token

    // mint the debt token and transfer it to the pair
    TokenAmount[] memory debtsToMint = new TokenAmount[](1);
    debtsToMint[0] = TokenAmount(path[0], amounts[0]);
    borrowerOperations.increaseDebt(msg.sender, pair, debtsToMint, _mintMeta);

    // execute the swap
    _swap(amounts, path, to);

    return amounts;
  }

  // --- Helper functions ---

  function getReserves(
    address tokenA,
    address tokenB
  ) internal view returns (uint reserveA, uint reserveB, uint32 swapFee) {
    ISwapPair pair = ISwapPair(getPair[tokenA][tokenB]);
    swapFee = pair.getSwapFee();

    (uint reserve0, uint reserve1, ) = pair.getReserves();
    (address token0, ) = sortTokens(tokenA, tokenB);
    (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
  }

  function sortTokens(address tokenA, address tokenB) internal view returns (address token0, address token1) {
    if (tokenA == tokenB) revert IdenticalAddresses();
    if (tokenA == address(0) || tokenB == address(0)) revert ZeroAddress();

    address stableCoin = address(debtTokenManager.getStableCoin());
    if (tokenA == stableCoin) return (tokenA, tokenB);
    if (tokenB == stableCoin) return (tokenB, tokenA);
    return tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
  }

  function safeTransferFrom(address token, address from, address to, uint256 value) internal {
    // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
    (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
    if (!success || (data.length > 0 && abi.decode(data, (bool)) == false)) revert TransferFromFailed();
  }
}
","keccak256":"0x69f470a6d718bb1e72d4e9d7b84909f9c2d8888db3dd96d41c672b29c3e1a901","license":"MIT"},"contracts/SwapPair.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import './Dependencies/LiquityMath.sol';
import './Dependencies/LiquityBase.sol';
import './Dependencies/UQ112x112.sol';
import './Interfaces/ISwapPair.sol';
import './Interfaces/ISwapOperations.sol';
import './Interfaces/ISwapCallee.sol';
import './SwapERC20.sol';
import './Interfaces/IPriceFeed.sol';
import './Interfaces/IDebtTokenManager.sol';

contract SwapPair is ISwapPair, SwapERC20, LiquityBase {
  using UQ112x112 for uint224;

  uint public constant MINIMUM_LIQUIDITY = 10 ** 3;
  bytes4 private constant SELECTOR = bytes4(keccak256(bytes('transfer(address,uint256)')));

  address public operations;
  IPriceFeed public priceFeed;
  IDebtTokenManager public debtTokenManager;

  address public token0;
  address public token1;

  uint112 private reserve0; // uses single storage slot, accessible via getReserves
  uint112 private reserve1; // uses single storage slot, accessible via getReserves
  uint32 private blockTimestampLast; // uses single storage slot, accessible via getReserves

  uint public price0CumulativeLast;
  uint public price1CumulativeLast;

  constructor() {
    operations = msg.sender;
  }

  // called once by the operations at time of deployment
  function initialize(address _token0, address _token1, address _debtTokenManager, address _priceFeedAddress) external {
    if (msg.sender != operations) revert Forbidden();

    token0 = _token0;
    token1 = _token1;
    debtTokenManager = IDebtTokenManager(_debtTokenManager);
    priceFeed = IPriceFeed(_priceFeedAddress);
  }

  uint private unlocked = 1;
  modifier lock() {
    if (unlocked == 0) revert Locked();

    unlocked = 0;
    _;
    unlocked = 1;
  }

  function getReserves() public view returns (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) {
    _reserve0 = reserve0;
    _reserve1 = reserve1;
    _blockTimestampLast = blockTimestampLast;
  }

  function _safeTransfer(address token, address to, uint value) private {
    (bool success, bytes memory data) = token.call(abi.encodeWithSelector(SELECTOR, to, value));

    if (!success || (data.length > 0 && abi.decode(data, (bool)) == false)) revert TransferFailed();
  }

  // update reserves and, on the first call per block, price accumulators
  function _update(uint balance0, uint balance1, uint112 _reserve0, uint112 _reserve1) private {
    if (balance0 > type(uint112).max || balance1 > type(uint112).max) revert Overflow();

    uint32 blockTimestamp = uint32(block.timestamp % 2 ** 32);
    uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
    if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
      // * never overflows, and + overflow is desired
      price0CumulativeLast += uint(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) * timeElapsed;
      price1CumulativeLast += uint(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) * timeElapsed;
    }

    reserve0 = uint112(balance0);
    reserve1 = uint112(balance1);
    blockTimestampLast = blockTimestamp;
    emit Sync(reserve0, reserve1);
  }

  // this low-level function should be called from a contract which performs important safety checks
  function mint(address to) external lock returns (uint liquidity) {
    _requireCallerIsOperations();

    (uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
    uint balance0 = IERC20(token0).balanceOf(address(this));
    uint balance1 = IERC20(token1).balanceOf(address(this));
    uint amount0 = balance0 - _reserve0;
    uint amount1 = balance1 - _reserve1;

    uint _totalSupply = totalSupply; // gas savings
    if (_totalSupply == 0) {
      liquidity = LiquityMath._sqrt(amount0 * amount1) - MINIMUM_LIQUIDITY;
      _mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
    } else {
      liquidity = LiquityMath._min((amount0 * _totalSupply) / _reserve0, (amount1 * _totalSupply) / _reserve1);
    }

    if (liquidity == 0) revert InsufficientLiquidityMinted();
    _mint(to, liquidity);
    _update(balance0, balance1, _reserve0, _reserve1);

    emit Mint(msg.sender, amount0, amount1);
  }

  // this low-level function should be called from a contract which performs important safety checks
  // directly burns debt tokens if the user has any left to repay
  function burn(
    address to,
    uint liquidity,
    uint debt0,
    uint debt1
  ) external lock returns (uint amount0, uint amount1, uint burned0, uint burned1) {
    _requireCallerIsOperations();

    (uint112 _reserve0, uint112 _reserve1, ) = getReserves();

    uint balance0 = IERC20(token0).balanceOf(address(this));
    uint balance1 = IERC20(token1).balanceOf(address(this));

    {
      uint _totalSupply = totalSupply; // gas savings
      amount0 = (liquidity * balance0) / _totalSupply; // using balances ensures pro-rata distribution
      amount1 = (liquidity * balance1) / _totalSupply; // using balances ensures pro-rata distribution

      if (amount0 == 0 || amount1 == 0) revert InsufficientLiquidityBurned();
      _burn(to, liquidity);
    }

    // check if the user has any debts left to repay
    burned0 = LiquityMath._min(debt0, amount0);
    burned1 = LiquityMath._min(debt1, amount1);
    if (burned0 != 0) IDebtToken(token0).burn(address(this), burned0);
    if (burned1 != 0) IDebtToken(token1).burn(address(this), burned1);

    // payout whats left
    _safeTransfer(token0, to, amount0 - burned0);
    _safeTransfer(token1, to, amount1 - burned1);

    balance0 = IERC20(token0).balanceOf(address(this));
    balance1 = IERC20(token1).balanceOf(address(this));
    _update(balance0, balance1, _reserve0, _reserve1);

    emit Burn(msg.sender, amount0, amount1, to);
  }

  // fee is returned in 1e6 (SWAP_FEE_PRECISION)
  function getSwapFee() public view override returns (uint32 swapFee) {
    // find stable coin
    address nonStableCoin = token1;
    if (!debtTokenManager.isDebtToken(nonStableCoin)) return SWAP_BASE_FEE; // no dynamic fee if the pool is not an stable/stock pool
    if (totalSupply == 0) return SWAP_BASE_FEE; //inital mint

    // query prices
    uint oraclePrice = priceFeed.getPrice(nonStableCoin);
    uint dexPrice = (reserve0 * DECIMAL_PRECISION) / reserve1; // todo does the token digits matter here?

    if (oraclePrice < dexPrice) return SWAP_BASE_FEE;
    uint priceRatio = (oraclePrice * DECIMAL_PRECISION) / dexPrice;
    return uint32((priceRatio * SWAP_BASE_FEE) / DECIMAL_PRECISION); // todo missing real fee calculation
  }

  // this low-level function should be called from a contract which performs important safety checks
  function swap(uint amount0Out, uint amount1Out, address to) external lock {
    _requireCallerIsOperations();
    if (amount0Out == 0 && amount1Out == 0) revert InsufficientOutputAmount();
    if (amount0Out > reserve0 || amount1Out > reserve1) revert InsufficientLiquidity();

    uint balance0;
    uint balance1;
    address _token0 = token0;
    address _token1 = token1;
    {
      // scope for _token{0,1}, avoids stack too deep errors
      if (to == _token0 || to == _token1) revert InvalidTo();

      if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
      if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
      balance0 = IERC20(_token0).balanceOf(address(this));
      balance1 = IERC20(_token1).balanceOf(address(this));
    }

    uint amount0In = balance0 > reserve0 - amount0Out ? balance0 - (reserve0 - amount0Out) : 0;
    uint amount1In = balance1 > reserve1 - amount1Out ? balance1 - (reserve1 - amount1Out) : 0;
    if (amount0In == 0 && amount1In == 0) revert InsufficientInputAmount();

    uint32 currentSwapFee = getSwapFee();
    {
      // scope for reserve{0,1}Adjusted, avoids stack too deep errors
      uint balance0Adjusted = balance0 * SWAP_FEE_PRECISION - (amount0In * currentSwapFee);
      uint balance1Adjusted = balance1 * SWAP_FEE_PRECISION - (amount1In * currentSwapFee);
      if (balance0Adjusted * balance1Adjusted < uint(reserve0) * uint(reserve1) * (uint(SWAP_FEE_PRECISION) ** 2))
        revert K();
    }

    // gov swap fee payment
    if (amount0In > 0) {
      uint amount0GovFee = (amount0In * currentSwapFee * GOV_SWAP_FEE) / (uint(SWAP_FEE_PRECISION) ** 2);
      _safeTransfer(_token0, GOV_STAKING_ADDRESS, amount0GovFee);
      balance0 -= amount0GovFee;
    }
    if (amount1In > 0) {
      uint amount1GovFee = (amount1In * currentSwapFee * GOV_SWAP_FEE) / (uint(SWAP_FEE_PRECISION) ** 2);
      _safeTransfer(_token1, GOV_STAKING_ADDRESS, amount1GovFee);
      balance1 -= amount1GovFee;
    }

    _update(balance0, balance1, reserve0, reserve1);
    emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, currentSwapFee, to);
  }

  // force balances to match reserves
  function skim(address to) external lock {
    address _token0 = token0; // gas savings
    address _token1 = token1; // gas savings
    _safeTransfer(_token0, to, IERC20(_token0).balanceOf(address(this)) - reserve0);
    _safeTransfer(_token1, to, IERC20(_token1).balanceOf(address(this)) - reserve1);
  }

  // force reserves to match balances
  function sync() external lock {
    _update(IERC20(token0).balanceOf(address(this)), IERC20(token1).balanceOf(address(this)), reserve0, reserve1);
  }

  function _requireCallerIsOperations() internal view {
    if (msg.sender != address(operations)) revert NotFromSwapOperations();
  }
}
